function similarText(f,c,h){if(f===null||c===null||typeof f==="undefined"||typeof c==="undefined"){return 0}f+="";c+="";var k=0;var i=0;var j=0;var e=f.length;var m=c.length;var b;var a;var d;var g;for(b=0;b<e;b++){for(a=0;a<m;a++){for(d=0;(b+d<e)&&(a+d<m)&&(f.charAt(b+d)===c.charAt(a+d));d++){}if(d>j){j=d;k=b;i=a}}}g=j;if(g){if(k&&i){g+=similarText(f.substr(0,k),c.substr(0,i))}if((k+j<e)&&(i+j<m)){g+=similarText(f.substr(k+j,e-k-j),c.substr(i+j,m-i-j))}}if(!h){return g}return(g*200)/(e+m)};